import React, { useEffect, useState } from 'react';
import { getRestaurants,searchRestaurantsByLocation } from '../services/apiService';
import { useNavigate } from 'react-router-dom';
import { detectFoodAndCuisine } from '../services/apiService';
import './HomePage.css';

const HomePage: React.FC = () => {
    const [restaurants, setRestaurants] = useState<any[]>([]);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(478); // Adjust based on backend logic
    const [latitude, setLatitude] = useState('');
    const [longitude, setLongitude] = useState('');
    const [radius, setRadius] = useState('');
    const [food, setFood] = useState<string | null>(null);
    const [cuisine, setCuisine] = useState<string | null>(null);
    const [image, setImage] = useState<File | null>(null);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchRestaurants = async () => {
            try {
                const data = await getRestaurants(page, 20); // Fetch 10 restaurants per page
                setRestaurants(data);
            } catch (error) {
                console.error('Error fetching restaurants:', error);
            }
        };
        fetchRestaurants();
    }, [page]);

    const handleRestaurantClick = (id: string) => {
        navigate(`/restaurant/${id}`);
    };

    const handleNextPage = () => {
        if (page < totalPages) setPage(page + 1);
    };

    const handlePreviousPage = () => {
        if (page > 1) setPage(page - 1);
    };

    const handleSearch = async () => {
        if (!latitude || !longitude || !radius) {
            alert('Please enter latitude, longitude, and radius');
            return;
        }

        try {
            const data = await searchRestaurantsByLocation(
                parseFloat(latitude),
                parseFloat(longitude),
                parseFloat(radius)
            );
            setRestaurants(data);
        } catch (error) {
            console.error('Error searching restaurants by location:', error);
        }
    };

    const handleImageUpload = async () => {
        if (!image) {
            alert('Please upload an image');
            return;
        }

        try {
            const data = await detectFoodAndCuisine(image);
            setFood(data.food);
            setCuisine(data.cuisine);
        } catch (error) {
            console.error('Error detecting food and cuisine:', error);
        }
    };

    return (
        <div className="home-page">
            <h1>Restaurants</h1>
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Latitude"
                    value={latitude}
                    onChange={(e) => setLatitude(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Longitude"
                    value={longitude}
                    onChange={(e) => setLongitude(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Radius (in km)"
                    value={radius}
                    onChange={(e) => setRadius(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>
            <div className="restaurant-list">
                {restaurants.map((restaurant) => (
                    <div
                        key={restaurant.id}
                        className="restaurant-card"
                        onClick={() => handleRestaurantClick(restaurant.id)}
                    >
                        <h2>{restaurant.name}</h2>
                        <p>{restaurant.cuisine.join(', ')}</p>
                        <p>
                            <span
                                className="rating"
                                style={{ backgroundColor: restaurant.rating_color }}
                            >
                                {restaurant.rating}
                            </span>
                        </p>
                    </div>
                ))}
            </div>
            <div className="pagination">
                <button onClick={handlePreviousPage} disabled={page === 1}>
                    Previous
                </button>
                <span>Page {page}</span>
                <button onClick={handleNextPage} disabled={page === totalPages}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default HomePage;
